import { doc, getDoc, getFirestore, setDoc } from "firebase/firestore";
import * as i0 from "@angular/core";
import * as i1 from "@angular/fire/compat/firestore";
export class DatabaseService {
    constructor(fireservices) {
        this.fireservices = fireservices;
        this.db = getFirestore();
    }
    // Write admin config settings in db
    async newConfig(users, groups, last) {
        await setDoc(doc(this.db, 'Configuration', 'configID'), {
            Groups: groups,
            Last: last,
            Users: users
        });
    }
    // Read admin config settings from db
    async getConfig() {
        const docRef = doc(this.db, "Configuration", "configID");
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
            console.log("Document data:", docSnap.data());
        }
        else {
            console.log("No such document!");
        }
    }
    // Read nb groups created from db
    async createdGrp() {
        const docRef = doc(this.db, "Groups", "groupsCreated");
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
            return docSnap.data();
        }
        else {
            return -1;
        }
    }
}
