import { __decorate } from "tslib";
import { Component } from '@angular/core';
let MyGroupComponent = class MyGroupComponent {
    constructor(databaseService) {
        this.databaseService = databaseService;
        this.groupName = '';
        this.users = [];
        this.noGroup = false;
    }
    ngOnInit() {
        // let username = this.userLogin.username;
        this.groupInfos('Naruto');
    }
    async groupInfos(username) {
        await this.databaseService.getGroupName(username).then((res) => {
            this.groupName = res;
        });
        this.groupName === '0' ? this.noGroup = true :
            await this.databaseService.getUsers(this.groupName).then((res) => {
                res.forEach((data) => {
                    for (let i = 0; i < data.length; i++) {
                        this.users.push(data[i]);
                    }
                });
            });
    }
    quitGroup(username = 'Naruto') {
    }
};
MyGroupComponent = __decorate([
    Component({
        selector: 'app-my-group',
        templateUrl: './my-group.component.html',
        styleUrls: ['./my-group.component.css']
    })
], MyGroupComponent);
export { MyGroupComponent };
