import { __decorate } from "tslib";
import { Component } from '@angular/core';
let OverviewComponent = class OverviewComponent {
    constructor(databaseServices) {
        this.databaseServices = databaseServices;
        this.list = [];
        this.length = 0;
    }
    ngOnInit() {
        this.getCreated();
    }
    async getCreated() {
        let data = await this.databaseServices.createdGrp();
        if (data == 0) {
            document.getElementById("no-group").style.display = "inline";
        }
        else {
            this.databaseServices.getCreatedGroups();
            await this.databaseServices.getCreatedGroups().then((res) => {
                let j = 0;
                res.forEach((data) => {
                    // console.log("DATA = ", data);
                    // for (let i=0; i<data.length; i++){
                    //   console.log("Group = ",data, data[i]);
                    // }
                    this.list[j] = data;
                    j++;
                });
            });
            this.length = this.list.length;
            console.log(this.list);
        }
    }
};
OverviewComponent = __decorate([
    Component({
        selector: 'app-overview',
        templateUrl: './overview.component.html',
        styleUrls: ['./overview.component.css']
    })
], OverviewComponent);
export { OverviewComponent };
